package com.yash_desai;import java.util.Scanner;import java.lang.Character;public class Character_Wrapper_class {    public static void main(String[] args) {        Scanner S = new Scanner(System.in);        Character C = null;        System.out.print("1. char charValue()\n" +                "\t:: Returns the value of this Character object.\n" +                "2. int compareTo(Character anotherCharacter)\n" +                "\t:: Compares two Character objects numerically.\n"+                "3. boolean equals(Object obj)\n" +                "\t:: Compares this object against the specified object.\n" +                "4. int hashCode()\n" +                "\t:: Returns a hash code for this Character.\n" +                "5. String toString()\n" +                "\t:: Returns a String object representing this Character's value.\n\n" +                "Enter the Option : ");        switch(S.nextByte()) {            case 1: {                C=MSG();                System.out.print("charValue() Return Value : "+C.charValue());                break;            }            case 2: {                System.out.print("#1 : ");                C=MSG();                System.out.print("#2 : ");                Character C2=MSG();                if(C.compareTo(C2)==0)                    System.out.println("They both have same character.");                else if(C.compareTo(C2)<0)                    System.out.println("Character Object #1 < Character Object #2");                else System.out.println("Character Object #1 > Character Object #2");                break;            }            case 3: {                System.out.print("#1 : ");                C=MSG();                System.out.print("#2 : ");                Character C2=MSG();                if(C.equals(C2)==true)                    System.out.println("They both have same character.");                else System.out.println("They have different character.");                break;            }            case 4: {                C=MSG();                System.out.println("Hash Code : "+C.hashCode());                break;            }            case 5: {                C=MSG();                String STR = C.toString();                System.out.println("String Value : "+STR);                break;            }            default:                System.out.println("Invalid Input...");                break;        }    }    static Character MSG() {        Scanner S = new Scanner(System.in);        System.out.print("Enter the Character Object Value : ");        return S.next().charAt(0);    }}